var $_documentContainer=document.createElement("div");$_documentContainer.setAttribute("style","display: none;");$_documentContainer.innerHTML="<dom-module id=\"fancy-carousel-shared-styles\">\n  <template>\n    <style>\n      button {\n        position: absolute;\n        top: calc(50% - 20px);\n        padding: 0;\n        line-height: 40px;\n        border: none;\n        background: none;\n        color: #DDD;\n        font-size: 40px;\n        font-weight: bold;\n        opacity: 0.7;\n      }\n\n      button:hover,\n      button:focus {\n        opacity: 1;\n      }\n\n      button[disabled] {\n        opacity: 0.4;\n      }\n\n      #content-wrapper ::content > .spread {\n        -webkit-mask-size: 2300% 100%;\n        mask-size: 2300% 100%;\n        -webkit-animation: mask-play 1.4s steps(22) forwards;\n        animation: mask-play 1.4s steps(22) forwards;\n      }\n\n      #content-wrapper ::content > .paint {\n        -webkit-mask-size: 3000% 100%;\n        mask-size: 3000% 100%;\n        -webkit-animation: mask-play 1.4s steps(29) forwards;\n        animation: mask-play 1.4s steps(29) forwards;\n      }\n\n      #content-wrapper ::content > .shift {\n        -webkit-mask-size: 7500% 100%;\n        mask-size: 7500% 100%;\n        -webkit-animation: mask-play 1.4s steps(74) forwards;\n        animation: mask-play 1.4s steps(74) forwards;\n      }\n\n      #content-wrapper ::content > .collapse {\n       -webkit-mask-size: 8400% 100%;\n          mask-size: 8400% 100%;\n        -webkit-animation: mask-play 1.4s steps(83) forwards;\n          animation: mask-play 1.4s steps(83) forwards;\n      }\n\n      #content-wrapper ::content > img {\n        position: absolute;\n        width: 100%;\n        height: 100%;\n      }\n\n      @-webkit-keyframes mask-play {\n        from {\n        -webkit-mask-position: 0% 0;\n        mask-position: 0% 0;\n        }\n        to {\n        -webkit-mask-position: 100% 0;\n        mask-position: 100% 0;\n        }\n      }\n\n      @keyframes mask-play {\n        from {\n        -webkit-mask-position: 0% 0;\n        mask-position: 0% 0;\n        }\n        to {\n        -webkit-mask-position: 100% 0;\n        mask-position: 100% 0;\n        }\n      }\n    </style>\n  </template>\n</dom-module>";document.head.appendChild($_documentContainer);