/**
 * Utility that makes it possible to hide fields when a checkbox is unchecked
 */(function($){function setupHiding(){var $toggler=$(this),$subject=$($toggler.data("h5p-visibility-subject-selector")),toggle=function toggle(){$subject.toggle($toggler.is(":checked"))};// Getting the field which should be hidden:
$toggler.change(toggle);toggle()}function setupRevealing(){var $button=$(this),$input=$("#"+$button.data("control"));// Getting the field which should have the value:
if(!$input.data("value")){$button.remove();return}// Setup button action
var revealed=!1,text=$button.html();$button.click(function(){if(revealed){$input.val("");$button.html(text);revealed=!1}else{$input.val($input.data("value"));$button.html($button.data("hide"));revealed=!0}})}$(document).ready(function(){// Get the checkboxes making other fields being hidden:
$(".h5p-visibility-toggler").each(setupHiding);// Get the buttons making other fields have hidden values:
$(".h5p-reveal-value").each(setupRevealing)})})(H5P.jQuery);