export class ESGlobalBridge{constructor(){this.load=(name,location)=>{if(!window.ESGlobalBridge.imports[name]){return new Promise((resolve,reject)=>{const script=document.createElement("script");script.src=location;script.setAttribute("data-name",name);window.ESGlobalBridge.imports[name]=location;script.onload=()=>{resolve(window.ESGlobalBridge.imports[name]);window.ESGlobalBridge.imports[name]=!0;setTimeout(()=>{const evt=new CustomEvent(`es-bridge-${name}-loaded`,{bubbles:!0,cancelable:!0,detail:{name:name,location:location}});document.dispatchEvent(evt)},100)};script.onerror=()=>{reject(new Error(`Failed to load ${name} script with location ${location}.`));delete window.ESGlobalBridge.imports[name];window.ESGlobalBridge.imports[name]=!1};document.documentElement.appendChild(script)})}}}}window.ESGlobalBridge=window.ESGlobalBridge||{};window.ESGlobalBridge.imports=window.ESGlobalBridge.imports||{};window.ESGlobalBridge.requestAvailability=()=>{if(!window.ESGlobalBridge.instance){window.ESGlobalBridge.instance=new ESGlobalBridge}};