import{LitElement,html,css}from"../../../lit-element/lit-element.js";import"./hax-blox-browser-item.js";class HaxBloxBrowser extends LitElement{static get styles(){return[css`
        :host {
          display: block;
        }
        hax-blox-browser-item {
          margin: 10px;
          -webkit-transition: 0.3s all linear;
          transition: 0.3s all linear;
        }
      `]}constructor(){super(),this.bloxList=[]}render(){return html`
      ${this.bloxList.map(blox=>html`
          <div class="blox-container">
            <hax-blox-browser-item
              .index="${blox.index}"
              .layout="${blox.details.layout}"
              .title="${blox.details.title}"
              .tag="${blox.details.tag}"
              .icon="${blox.details.icon}"
              .author="${blox.details.author}"
              .teaser="${blox.details.teaser}"
              .description="${blox.details.description}"
              .examples="${blox.details.examples}"
              .status="${blox.details.status}"
              .blox="${blox.blox}"
            ></hax-blox-browser-item>
          </div>
        `)}
    `}static get tag(){return"hax-blox-browser"}static get properties(){return{bloxList:{type:Array}}}}window.customElements.define(HaxBloxBrowser.tag,HaxBloxBrowser);export{HaxBloxBrowser};