import{html,PolymerElement}from"../../../../../@polymer/polymer/polymer-element.js";import{store}from"../../core/haxcms-site-store.js";import{autorun,toJS}from"../../../../../mobx/lib/mobx.module.js";class SiteTitle extends PolymerElement{static get tag(){return"site-title"}static get template(){return html`
      <style>
        :host {
          display: block;
          text-rendering: optimizelegibility;
          position: relative;
        }
        a {
          @apply --site-title-link;
        }
        a h1 {
          text-rendering: optimizelegibility;
          @apply --site-title-heading;
        }
      </style>
      <a href$="[[homeLink]]">
        <h1>[[siteTitle]]</h1>
      </a>
    `}static get properties(){return{siteTitle:{type:String},homeLink:{type:String}}}connectedCallback(){super.connectedCallback();this.__disposer=autorun(()=>{this.siteTitle=toJS(store.siteTitle)});this.__disposer2=autorun(()=>{this.homeLink=toJS(store.homeLink)})}disconnectedCallback(){super.disconnectedCallback();this.__disposer();this.__disposer2()}}window.customElements.define(SiteTitle.tag,SiteTitle);export{SiteTitle};