import{html,PolymerElement}from"../../../../../@polymer/polymer/polymer-element.js";import{store}from"../../core/haxcms-site-store.js";import{autorun,toJS}from"../../../../../mobx/lib/mobx.module.js";import"../../../../../@polymer/iron-icon/iron-icon.js";import"../../../../../@polymer/paper-tooltip/paper-tooltip.js";class SiteTitle extends PolymerElement{static get tag(){return"site-title"}static get template(){return html`
      <style>
        :host {
          display: block;
          text-rendering: optimizelegibility;
          position: relative;
        }
        a {
          @apply --site-title-link;
        }
        a:hover,
        a:focus,
        a:active {
          @apply --site-title-link-hover;
        }
        a h1 {
          text-rendering: optimizelegibility;
          @apply --site-title-heading;
        }
        iron-icon {
          @apply --site-title-icon;
        }
        paper-tooltip {
          @apply --site-title-tooltip;
        }
      </style>
      <a id="btn" href$="[[homeLink]]" title$="Go to [[siteTitle]]">
        <iron-icon hidden$="[[!icon]]" icon="[[icon]]"></iron-icon>
        <h1 hidden$="[[notitle]]">[[siteTitle]]</h1>
      </a>
      <paper-tooltip for="btn" position="[[position]]" offset="14">
        [[label]]
      </paper-tooltip>
    `}static get properties(){return{siteTitle:{type:String},homeLink:{type:String},label:{type:String,value:"Home"},position:{type:String,value:"bottom"},icon:{type:String,value:!1},notitle:{type:Boolean,reflectToAttribute:!0,value:!1}}}connectedCallback(){super.connectedCallback();this.__disposer=[];autorun(reaction=>{this.siteTitle=toJS(store.siteTitle);this.__disposer.push(reaction)});autorun(reaction=>{this.homeLink=toJS(store.homeLink);this.__disposer.push(reaction)})}disconnectedCallback(){super.disconnectedCallback();for(var i in this.__disposer){this.__disposer[i].dispose()}}}window.customElements.define(SiteTitle.tag,SiteTitle);export{SiteTitle};