import{html,PolymerElement}from"../../@polymer/polymer/polymer-element.js";import{SchemaBehaviors}from"../schema-behaviors/schema-behaviors.js";class LrnTable extends(SchemaBehaviors(PolymerElement)){constructor(){super(),import("../csv-render/csv-render.js")}static get template(){return html`
      <style>
        :host {
          display: block;
        }
        .hidden-title {
          display: none;
        }
      </style>
      <div typeof="oer:SupportingMaterial">
        <div class="hidden-title" property="oer:name">[[title]]</div>
        <div property="oer:description">
          <slot></slot>
          <csv-render
            data-source="[[csvFile]]"
            caption="[[title]]"
            summary="[[description]]"
          ></csv-render>
        </div>
      </div>
    `}static get tag(){return"lrn-table"}static get properties(){return{title:{type:String},csvFile:{type:String},description:{type:String}}}static get haxProperties(){return{canScale:!0,canPosition:!0,canEditSource:!1,gizmo:{title:"CSV table",description:"This can generate a table from a CSV file no matter where it is located.",icon:"editor:border-all",color:"green",groups:["Presentation","Table","Data"],handles:[{type:"csv",source:"csvFile"}],meta:{author:"LRNWebComponents"}},settings:{quick:[{property:"csvFile",title:"Source",description:"The URL for this csv file.",inputMethod:"textfield",icon:"link",required:!0},{property:"title",title:"Title",description:"Title for the table to be generated.",inputMethod:"textfield",icon:"editor:title"},{property:"description",title:"Description",description:"More detailed description for improved accessibility of the table data.",inputMethod:"textfield",icon:"editor:short-text"}],configure:[{property:"csvFile",title:"Source",description:"The URL for this csv file.",inputMethod:"haxupload",required:!0},{property:"title",title:"Title",description:"Title for the table to be generated.",inputMethod:"textfield"},{property:"description",title:"Description",description:"More detailed description for improved accessibility of the table data.",inputMethod:"textfield"}],advanced:[]}}}}window.customElements.define(LrnTable.tag,LrnTable);export{LrnTable};