import{html,Polymer}from"../../../@polymer/polymer/polymer-legacy.js";import"../../paper-avatar/paper-avatar.js";import"../../lrn-icons/lrn-icons.js";import"../../../@polymer/iron-icons/iron-icons.js";Polymer({_template:html`
    <style>
      :host {
        display: block;
      }
      :host > div > * {
        vertical-align: middle;
      }
      .text-label {
        margin-left: 8px;
      }
      .text-label-only {
        margin-left: 0;
      }
    </style>
    <div>
      <template is="dom-if" if="[[avatar]]">
        <paper-avatar src\$="[[avatar]]"></paper-avatar>
      </template>
      <template is="dom-if" if="[[icon]]">
        <lrn-icon icon="[[icon]]"></lrn-icon>
      </template>
      <template is="dom-if" if="[[text]]">
        <span class\$="[[_getTextLabelClass()]]">[[text]]</span>
      </template>
    </div>
    <div><slot name="button"></slot><slot></slot></div>
  `,is:"lrnsys-button-inner",properties:{icon:{type:String},avatar:{type:String},text:{type:String}},_getTextLabelClass:function(){if(!this.avatar&&!this.icon){return"text-label-only"}return"text-label"}});