class PageScrollPosition extends HTMLElement{attachedCallback(){this.value=0;let element=document,valueChangedEvent=new CustomEvent("value-changed",{detail:{value:0}});this.dispatchEvent(valueChangedEvent),element.addEventListener("scroll",()=>{let c=document.documentElement.scrollTop/(document.documentElement.scrollHeight-document.documentElement.clientHeight)*100;this.value=c,valueChangedEvent=new CustomEvent("value-changed",{detail:{value:this.value}}),this.dispatchEvent(valueChangedEvent)})}}window.customElements.define("page-scroll-position",PageScrollPosition);