import{html,PolymerElement}from"../../../../@polymer/polymer/polymer-element.js";import{SimpleColors}from"../../simple-colors.js";import"./simple-colors-swatch-info.js";import"../../../simple-modal/lib/simple-modal-template.js";class simpleColorsSwatches extends SimpleColors{static get template(){return html`
      <style is="custom-style" include="simple-colors">
        :host {
          display: block;
          margin: 15px 0;
        }
        :host([hidden]) {
          display: none;
        }
        :host simple-modal-template {
          --simple-modal-titlebar-color: var(--simple-colors-swatch-contrast);
          --simple-modal-titlebar-background: var(--simple-colors-swatch-color);
        }
        :host .row {
          display: flex;
          align-items: stretch;
        }
        :host button {
          width: calc(8% - 6px);
          height: 80px;
          margin: 3px;
          border: 1px solid black;
          border-radius: 3px;
          padding: 0;
          display: flex;
          flex-direction: column;
          justify-content: flex-end;
          align-items: stretch;
        }
        :host button div {
          margin: 0;
          padding: 2px;
          font-size: 10px;
          background: black;
          color: white;
          width: calc(100% - 4px);
        }
        :host button:focus,
        :host button:hover {
          opacity: 0.7;
          cursor: pointer;
        }
      </style>
      <div>
        <p>Click on the swatches above to learn more.</p>
        <div class="rows">
          <template is="dom-repeat" items="[[_getOptions(colors)]]" as="color">
            <div class="row">
              <template is="dom-repeat" items="[[colors.grey]]">
                <button
                  id="[[color]]_[[index]]"
                  controls="swatchinfo"
                  style$="background-color: [[_getHex(color,index)]];"
                  title$="[[color]]-[[_getShade(index)]]"
                  on-click="_handleTap"
                >
                  <div>[[color]]-[[_getShade(index)]]</div>
                </button>
              </template>
            </div>
          </template>
        </div>
        <simple-modal-template id="modal" title="[[swatchName]]">
          <p slot="content">
            <simple-colors-swatch-info
              swatch-id$="[[swatchId]]"
              swatch-name$="[[swatchName]]"
            >
            </simple-colors-swatch-info>
          </p>
        </simple-modal-template>
      </div>
    `}static get properties(){return{swatchId:{name:"swatchId",type:"String",value:null},swatchName:{name:"swatchName",type:"String",value:null}}}static get behaviors(){return[SimpleColors]}static get tag(){return"simple-colors-swatches"}connectedCallback(){super.connectedCallback()}ready(){super.ready()}_getShade(index){return parseInt(index)+1}_getHex(color,index){return this.colors[color][index]}_handleTap(e){this.swatchId=e.path[0].getAttribute("id");this.swatchName=e.path[0].getAttribute("title");this.$.modal.openModal(e.path[0])}_getOptions(obj){return Object.keys(obj)}}export{simpleColorsSwatches};window.customElements.define(simpleColorsSwatches.tag,simpleColorsSwatches);