export class UserActionBroker{valid(e){return["click","hover","mousedown","mouseup","visibility","keypress","keydown","keyup","focusin","focusout"].includes(e)}fire(e,o,s,r){s.eventType=o,r.dispatchEvent(new CustomEvent(e,{bubbles:!0,composed:!0,cancelable:!0,detail:s}))}}export const UABroker=new UserActionBroker;