export class RippleHandlers{constructor(e){this.activate=t=>{e().then(e=>{e&&e.activate(t)})},this.deactivate=()=>{e().then(e=>{e&&e.deactivate()})},this.handleFocus=()=>{e().then(e=>{e&&e.handleFocus()})},this.handleBlur=()=>{e().then(e=>{e&&e.handleBlur()})},this.handleMouseEnter=()=>{e().then(e=>{e&&e.handleMouseEnter()})},this.handleMouseLeave=()=>{e().then(e=>{e&&e.handleMouseLeave()})}}}