import{MIXIN_MATCH,VAR_ASSIGN}from"./common-regex.js";export function updateNativeProperties(element,properties){for(let p in properties)null===p?element.style.removeProperty(p):element.style.setProperty(p,properties[p])}export function getComputedStyleValue(element,property){const value=window.getComputedStyle(element).getPropertyValue(property);return value?value.trim():""}export function detectMixin(cssText){const has=MIXIN_MATCH.test(cssText)||VAR_ASSIGN.test(cssText);return MIXIN_MATCH.lastIndex=0,VAR_ASSIGN.lastIndex=0,has}