export function stripExtraTrailingSlash(t){for(;1!==t.length&&"/"===t.substr(-1);)t=t.substr(0,t.length-1);return t}export function parseQuery(t){return t?JSON.parse('{"'+t.substring(1).replace(/&/g,'","').replace(/=/g,'":"')+'"}'):{}}export function parseParams(t,e){let r={};const n=t.split("/").filter((t=>""!=t)),p=e.split("/").filter((t=>""!=t));return n.map(((t,e)=>{/^:/.test(t)&&(r[t.substring(1)]=p[e])})),r}export function patternToRegExp(t){return t?new RegExp("^(|/)"+t.replace(/:[^\s/]+/g,"([\\wÀ-ÖØ-öø-ÿ-]+)")+"(|/)$"):new RegExp("(^$|^/$)")}export function testRoute(t,e){if(patternToRegExp(e).test(t))return!0}