import{reparentNodes}from"./dom.js";import{boundAttributeSuffix,lastAttributeNameRegex,marker,nodeMarker}from"./template.js";export class TemplateResult{constructor(strings,values,type,processor){this.strings=strings,this.values=values,this.type=type,this.processor=processor}getHTML(){const endIndex=this.strings.length-1;let html="";for(let i=0;i<endIndex;i++){const s=this.strings[i],match=lastAttributeNameRegex.exec(s);html+=match?s.substr(0,match.index)+match[1]+match[2]+boundAttributeSuffix+match[3]+marker:s+nodeMarker}return html+this.strings[endIndex]}getTemplateElement(){const template=document.createElement("template");return template.innerHTML=this.getHTML(),template}}export class SVGTemplateResult extends TemplateResult{getHTML(){return`<svg>${super.getHTML()}</svg>`}getTemplateElement(){const template=super.getTemplateElement(),content=template.content,svgElement=content.firstChild;return content.removeChild(svgElement),reparentNodes(content,svgElement.firstChild),template}}