import{html,PolymerElement}from"../@polymer/polymer/polymer-element.js";import{FlattenedNodesObserver}from"../@polymer/polymer/lib/utils/flattened-nodes-observer.js";window.customElements.define("paper-collapse-group",class PaperCollapseGroup extends PolymerElement{static get template(){return html`<slot id="content"></slot>`}static get properties(){return{_items:{type:Array,notify:!0,value:[]}}}ready(){super.ready(),this.addEventListener("toggle",e=>this._onToggle(e))}connectedCallback(){super.connectedCallback(),this._observer=new FlattenedNodesObserver(this.$.content,info=>{var addedItems=info.addedNodes.filter(node=>"paper-collapse-item"===node.nodeName.toLowerCase());if(addedItems.length>0){this._items=this._items.concat(addedItems);const updateEvent=new CustomEvent("update",{detail:null,bubbles:!0,composed:!0});this.dispatchEvent(updateEvent)}})}disconnectedCallback(){super.disconnectedCallback(),this._observer.disconnect()}_onToggle(e){this._items.forEach(item=>{item!==e.target&&(item.opened=!1)})}});