import{Observable}from"../Observable.js";import{Subscription}from"../Subscription.js";import{subscribeToArray}from"../util/subscribeToArray.js";export function fromArray(input,scheduler){if(!scheduler){return new Observable(subscribeToArray(input))}else{return new Observable(function(subscriber){var sub=new Subscription,i=0;sub.add(scheduler.schedule(function(){if(i===input.length){subscriber.complete();return}subscriber.next(input[i++]);if(!subscriber.closed){sub.add(this.schedule())}}));return sub})}}